default.ClothingDefineOutfit:
// Example of a custom outfit that uses the same settings as the default Underwear outfit
import {ClothingModel, ClothingSleeve, ClothingPiece} from '../.config/enums';

// Model is set to Hair to not replace the head and simply use the player’s hair as head piece
// localizationName is set to “CLT_Undies” which basically makes it show off in game as “Underwear” if your game is in English
Clothing.DefineOutfit("UnderwearNew", "CLT_Undies", ClothingModel.Hair, ClothingModel.P_SSleeves11, ClothingModel.none, ClothingModel.none, ClothingModel.SP_Briefs, ClothingModel.SP_Socks, ClothingSleeve.ShortSleeve, ClothingPiece.MultiPiece);

default.PedCreateXYZ:
// Spawning a ped with a model that was not loaded into memory previously
let pedName = PedModel.StudentGary;

Streaming.RequestPedModel(pedName);
wait(100)
ped = Char.Create(pedName, spawnX, spawnY, coords.z);

// It is however reccomended to instead precache the models, once, at the start of the script, not during runtime when spawning peds to avoid having to use a delay when spawning the peds.

default.PedGetPedToTypeAttitude:
// If you want to quickly check the attitude your target
// has towards the player faction and print it at the
// top center of the screen, you can use the following code:
import {PedType, TextPosition} from '../.config/enums';

let playerHandle = Player.GetChar();
let targetPed = playerHandle.getTargetPed();
let attitude = targetPed.getAttitudeTowardsFaction(PedType.Player1);

Text.PrintString(attitude, 1, TextPosition.MiddleTop);

default.PedIsDead:
// There are different ways to check if a ped died:
// You can use this to check if a ped is fully lying on the ground dead:
let pedIsDead = yourPed.isDead();

// If you want to check if the ped is playing the knocked out animation, you can use this:
let pedIsDead = false;

if (yourPed.isValid()) {
	pedIsDead = yourPed.isPlaying("/Global/HitTree/KnockOuts", true);
}

// Or you can check if the ped is at 0 health, which is exactly when the ped will get knocked out by doing the following:
let pedIsDead = yourPed.getHealth() <= 0;

default.PedIsInCombat:
// You can make a bool to check if a ped is attacking the player by doing this:
let playerHandle = Player.GetChar();
let attackingPlayer = (yourPed.isInCombat() && Number(yourPed.getTargetPed()) === Number(playerHandle));

default.PedIsTargetable:
// In the game this was used to check if Jimmy was targgeting a specific ped on a mission
// Usage:
let playerHandle = Player.GetChar();
let playerTargettingPed = playerHandle.IsTargettingPed(yourPed);

// Alternatively, you can also do:
let playerTargettingPed = Number(playerHandle.getTargetPed()) === Number(yourPed);

default.PedSetActionNode:
// Simple example that makes the player throw an apple with the frisbee animation upon pressing
// the attack button
import {Button, Device, WeaponModel} from '../../.config/enums';

let playerHandle = Player.GetChar();
let node = "/Global/Books/Books/Actions/Attacks/ThrowActions/FrisbeeThrow/ThrowStart";

if (Pad.IsButtonBeingPressed(Button.Attack, Device.Player1) && !playerHandle.isPlayingActionNode(node, true)) {
	playerHandle.setWeapon(WeaponModel.Apple);
	wait(100);
	playerHandle.playActionNode(node, "Globals/Thrown.act");
}

default.PedSetFlag:
// This example will make the player be able to get into cars and even drive them
import {PedFlag} from '../../.config/enums';

let playerHandle = Player.GetChar();

playerHandle.setFlag(PedFlag.CanDriveCars, true);

default.PedSetGrappleTarget:
// Since setting both the target and grappler to -1 doesn't work as intended (and can even crash the game)
// You can use this to achieve a more desired result:

// First get the player and target handle
let playerHandle = Player.GetChar();
let grappleTarget = playerHandle.getGrappleTarget();

// Then you can do this (makes both characters stop holding eachothers with no anim):
playerHandle.setGrappleTarget(-1);
grappleTarget.setActionNode("/Global/Ambient/Scripted/Empty/EmptyNode/TrueEmptyNode", "Act/Anim/Ambient.act");

// or this (pretty much same as above):
playerHandle.setActionNode("/Global/Ambient/Scripted/Empty/EmptyNode/TrueEmptyNode", "Act/Anim/Ambient.act");
grappleTarget.setActionNode("/Global/Ambient/Scripted/Empty/EmptyNode/TrueEmptyNode", "Act/Anim/Ambient.act");

// Or alternatively play this animation on the person that started the grab (makes the player, in this case, do the grapple release animation so it effectively makes the player release the target):
playerHandle.setActionNode("/Global/Actions/Grapples/GrappleReversals/StandingReversals/GrappleBreak/Break_GIVE", "Globals/Grapples.act");

default.PlayerLockButtonInputsExcept:
// Lock all inputs
Pad.LockInputsExcept(true);

// Lock all inputs except melee button
Pad.LockInputsExcept(true, Button.MeleeAttack);

// Lock all inputs except melee, grapple and jump
Pad.LockInputsExcept(true, Button.MeleeAttack, Button.Grapple, Button.Jump);

// Unlock inputs. You need to do this to make it go back to normal
Pad.LockInputsExcept(false);

default.SoundIsPreloadReady:
// Intended to be used for big files, and in a loop to make sure it doesn't play until it actually fully loaded.
// Preload the music
Audio.PreloadMusicNoLoop("1-06_HoboNIS.rsm", 1);

// Variable to track the start time
var startTime = Clock.GetGameTimer();

do {
    // If the timer exceeds 7000 ms, break the loop
    if (Clock.GetGameTimer() - startTime > 7000) {
        break;
    }
} while (!Audio.IsPreloadComplete());

Audio.PlayPreloadedMusic();

default.VehicleRequestModel:
// You can use this to make sure the game stops until the model is actually fully loaded
while (!Streaming.RequestVehicleModel(273)) {
      wait(0);
}
